package com.alok.projects.lovable_clone.entity;

// in lovable you'll get a link to preview the application that was being generated by LLM

import com.alok.projects.lovable_clone.enums.PreviewStatus;
import jakarta.persistence.*;
import lombok.AccessLevel;
import lombok.Getter;
import lombok.Setter;
import lombok.experimental.FieldDefaults;

import java.time.Instant;

@Entity
@FieldDefaults(level = AccessLevel.PRIVATE) // it'll make the fields private by default
@Getter
@Setter
public class Preview {
    @Id
    Long id;

    @OneToOne
    @JoinColumn(
            name = "project_id"
    )
    Project project;

    String namespace;
    String podName;
    String previewUrl;

    PreviewStatus status;

    Instant startedAt;
    Instant terminatedAt;

    Instant createdAt;
}
